    nightstalker.ui.adj.bottomleft = Adjustable.Container:new({name = 'Mindsim', x = '0%', y = '75%', width = '25%', height = '25%', fontSize = 12}) 



    nightstalker.ui.topright = Geyser.Label:new({ name = 'nightstalker.ui.topright', x = 0, y = 0, width = '100%', height = '100%', }, nightstalker.ui.adj.topright)
    nightstalker.ui.topright:setStyleSheet(boxcss:getCSS())

    nightstalker.ui.bottomright = Geyser.Label:new({ name = 'nightstalker.ui.bottomright', x = 0, y = 0, width = '100%', height = '100%', }, nightstalker.ui.adj.bottomright)
    nightstalker.ui.bottomright:setStyleSheet(boxcss:getCSS())

    nightstalker.ui.topleft = Geyser.Label:new({ name = 'nightstalker.ui.topleft',  x = 0, y = 0, width = '100%', height = '100%',  fontSize = 11 }, nightstalker.ui.adj.topleft)
    nightstalker.ui.topleft:setStyleSheet(boxcss:getCSS())

    nightstalker.ui.bottomleft = Geyser.Label:new({ name = 'nightstalker.ui.bottomleft',  x = 0, y = 0, width = '100%', height = '100%',  fontSize = 11 }, nightstalker.ui.adj.bottomleft)
    nightstalker.ui.bottomleft:setStyleSheet(boxcss:getCSS())
  end

function nightstalker.ui.create_gauges(class)
    nightstalker.ui.footer = Geyser.HBox:new({ name = "nightstalker.ui.footer", x = 0, y = 0, width = "100%", height = "100%", }, nightstalker.ui.bottom)

    nightstalker.ui.leftcolumn = Geyser.VBox:new({ name = "nightstalker.ui.leftcolumn", }, nightstalker.ui.footer)

    if class == 'Nanoseer' then
      nightstalker.ui.middlecolumn = Geyser.VBox:new({ name = "nightstalker.ui.middlecolumn", }, nightstalker.ui.footer)
    end

    nightstalker.ui.rightcolumn = Geyser.VBox:new({ name = "nightstalker.ui.rightcolumn", }, nightstalker.ui.footer)

    local backcss = CSSMan.new([[
      background-color: rgba(0,0,0,0);
      border-style: solid;
      border-color: grey;
      border-width: 1px;
      border-radius: 5px;
      margin: 5px;
    ]])

    local frontcss = CSSMan.new([[
      background-color: rgba(0,0,0,0);
      border-style: solid;
      border-color: grey;
      border-width: 1px;
      border-radius: 5px;
      margin: 5px;
    ]])

    local function gauge(name, column, bgc)
      nightstalker.ui[name] = Geyser.Gauge:new({ name = 'nightstalker.ui.'..name }, column)
      nightstalker.ui[name].back:setStyleSheet(backcss:getCSS())
      frontcss:set('background-color', bgc)
      nightstalker.ui[name].front:setStyleSheet(frontcss:getCSS())
      nightstalker.ui[name]:setValue(100,100)
    end

    gauge('health', nightstalker.ui.leftcolumn, 'brown')

    if class == 'Nanoseer' then
      gauge('nanites', nightstalker.ui.middlecolumn, 'navy')
      gauge('sanity', nightstalker.ui.rightcolumn, 'purple')
    elseif class == 'Engineer' then
      gauge('parts', nightstalker.ui.rightcolumn, 'blue')
    elseif class == 'B.E.A.S.T.' then
      gauge('plasma', nightstalker.ui.rightcolumn, 'firebrick')
    elseif class == 'Scoundrel' then
      gauge('bullets', nightstalker.ui.rightcolumn, 'orange')
    elseif class == 'Fury' then
      gauge('rage', nightstalker.ui.rightcolumn, 'red')
    end
  end

function Geyser.MiniConsole:clear()
  clearWindow(self.name)
end

function Geyser.MiniConsole:append()
  appendBuffer(self.name)
end

nightstalker.events = nightstalker.events or {}

function nightstalker.registerevent(name, event, func)
  if not nightstalker.events[name] then
    nightstalker.events[name] = registerAnonymousEventHandler(event, func)
  end
end

function nightstalker.killevent(name)
  if nightstalker.events[name] then
    return killAnonymousEventHandler(nightstalker.events[name])
  end
end

local ignore = {'Captaincy', 'Hacking', 'Art', 'Fashion', 'Cuisine', 'Jewelry', 'WeaponModding', 'ArmorModding', 'ShipModding', 'Furnishing', 'Mixology'}

function nightstalker.gmcpcharskillsgroups()
  if not nightstalker.skills then
    nightstalker.skills = {}
    table.foreach(gmcp.Char.Skills.Groups, function(key, skillset)
      if not table.contains(ignore, skillset.name) then
        sendGMCP(string.format('Char.Skills.Get %s', yajl.to_string({group=skillset.name})))
        nightstalker.skills[skillset.name] = {
          rank = tonumber(skillset.rank:match('(%d+)/%d+')),
          maxrank = tonumber(skillset.rank:match('%d+/(%d+)')),
          list = {}
        }
      end
    end)
  end
end

function nightstalker.gmcpcharskillslist()
  table.foreach(gmcp.Char.Skills.List.list, function(key, skill)
    sendGMCP(string.format('Char.Skills.Get %s', yajl.to_string({
      group = gmcp.Char.Skills.List.group:title(),
      name = skill
    })))
  end)
end

function nightstalker.gmcpcharskillsinfo()
  local group = gmcp.Char.Skills.Info.group:title()
  local skill = gmcp.Char.Skills.Info.skill:title()
  local lessons = gmcp.Char.Skills.Info.info:match('(%d+) Lessons')
  nightstalker.skills[group].list[#nightstalker.skills[group].list + 1] = {skill, tonumber(lessons)}
  if #nightstalker.skills[group].list == nightstalker.skills[group].maxrank then
    table.sort(nightstalker.skills[group].list, function(a,b) return a[2] < b[2] end)
    local sorted_list = nightstalker.skills[group].list
    nightstalker.skills[group].list = {}
    table.foreach(sorted_list, function(key, value)
      nightstalker.skills[group].list[#nightstalker.skills[group].list + 1] = value[1]
    end)
  end
end

function nightstalker.haveskill(skill, skillset)
  local rank = nightstalker.skills[skillset].rank
  local skill = table.index_of(nightstalker.skills[skillset].list, skill)
  return skill <= rank
end

nightstalker.registerevent('fetch groups', 'gmcp.Char.Skills.Groups', 'nightstalker.gmcpcharskillsgroups')
nightstalker.registerevent('fetch skills', 'gmcp.Char.Skills.List', 'nightstalker.gmcpcharskillslist')
nightstalker.registerevent('parse skills', 'gmcp.Char.Skills.Info', 'nightstalker.gmcpcharskillsinfo')

